function numToFloat(n){const[e,t]=Number(n).toExponential().split("e").map(n=>+n);return{mant:e,exp:t}}function roundToFixed(n,e=0){if("number"==typeof n&&"number"==typeof e&&Number.isInteger(e)&&(0===e||1===Math.sign(e))){const t=numToFloat(n),o=numToFloat(Math.round(+(t.mant+"e"+(t.exp+e))));return+(o.mant+"e"+(o.exp-e))}return NaN}module.exports=roundToFixed;